Actionscript.script:
import flash.events.MouseEvent;

stop();

start.addEventListener(MouseEvent.CLICK, startClicked);

function startClicked(event:MouseEvent):void
{
	var snd:StartSound = new StartSound();
	snd.play();
	gotoAndPlay(1, 'Intro');
}

import flash.events.MouseEvent;
import flash.text.TextFormat;

stop();

var startIndex:int;
var endIndex:int;

var formatSub:TextFormat = new TextFormat('Font 2');
formatSub.size = 12;
formatSub.bold = true;

text.text = 'You earned ' + getScore() + ' ';
text.text += 'score. For a couple of days you have tried out the life of a factory worker in China.';

startIndex = text.length;
text.text += '\n\nClick arrow for more information.';
endIndex = text.length;
text.setTextFormat(formatSub, startIndex, endIndex);

var txt:Array =  [	
	'Harsh treatment from managers is common at the Foxconn factory.\nWorkers are reminded they should obey all instructions by superiors without questioning.\nSometimes management sets traps to test the workers.\nFor example management can take away a component of a product. If the worker concerned cannot discover the flaw, he/she will be punished.\nPublic humiliation is a common punishment.',
	'In the Wintek factory workers used the poisonous chemical n-hexane to clean touch screens.\nWorkers got really sick and a number of them were taken into hospital on October the 26th 2010.\nEarlier alcohol was used but it was replaced by n-hexane as it evaporates faster and hence can increase the production rate.',
	'In the Foxconn factory monthly overtime working hours amounts to 120 hours during the peak production time.\nThe local labour law says the maximum is 36 overtime working hours per month.\nThe production target is usually 5000 pieces per day.\nAlthough Foxconn claims workers have a 10-minute break every 2 hours, a worker interviewed says there are no breaks in her department at all.'
];

next.addEventListener(MouseEvent.CLICK, function (event:MouseEvent) {
	if (txt.length > 0) {
		text.text = txt.pop();
		startIndex = text.length;
		if (txt.length == 0) {
			text.text += '\n\nClick arrow to try again.';
		} else {
			text.text += '\n\nClick arrow for more information.';
		}
		endIndex = text.length;
		text.setTextFormat(formatSub, startIndex, endIndex);
	} else {
		gotoAndPlay('start', 'Factory');
	}
});
